              <p style={{
                fontSize: "clamp(0.95rem, 1.8vw, 1.1rem)",
                color: "var(--text-muted)",
                maxWidth: "700px",
                margin: "0 auto"
              }}>
                Every role at Cehpoint directly contributes to delivering these cutting-edge services
              </p>
            </motion.div>

            <motion.div
              variants={containerVariants}
              initial="hidden"
              whileInView="visible"
              viewport={{ once: true }}
              style={{
                display: "grid",
                gridTemplateColumns: "repeat(auto-fit, minmax(min(100%, 280px), 1fr))",
                gap: "1.75rem",
                maxWidth: "1400px",
                margin: "0 auto"
              }}
            >
              {services.map((service, index) => (
                <motion.div
                  key={service.title}
                  variants={itemVariants}
                  onHoverStart={() => setHoveredService(index)}
                  onHoverEnd={() => setHoveredService(null)}
                  style={{
                    background: "linear-gradient(135deg, rgba(23, 23, 35, 0.8) 0%, rgba(30, 30, 45, 0.7) 100%)",
                    backdropFilter: "blur(20px)",
                    borderRadius: "20px",
                    border: "1px solid rgba(255, 255, 255, 0.08)",
                    padding: "2rem",
                    transition: "all 0.4s cubic-bezier(0.4, 0, 0.2, 1)",
                    position: "relative",
                    overflow: "hidden",
                    cursor: "pointer"
                  }}
                  whileHover={{ y: -8, borderColor: "rgba(167, 139, 250, 0.25)" }}
                >
                  <motion.div
                    style={{
                      position: "absolute",
                      top: 0,
                      left: 0,
                      right: 0,
                      bottom: 0,
                      background: service.gradient,
                      opacity: 0,
                      transition: "opacity 0.4s"
                    }}
                    animate={{
                      opacity: hoveredService === index ? 0.05 : 0
                    }}
                  />

                  <div style={{ position: "relative", zIndex: 1 }}>
                    <div style={{
                      fontSize: "3rem",
                      marginBottom: "1rem",
                      display: "flex",
                      alignItems: "center",
                      justifyContent: "center",
                      height: "80px"
                    }}>
                      {service.icon}
                    </div>

                    <h3 style={{
                      fontSize: "1.35rem",
                      fontWeight: 700,
                      marginBottom: "0.75rem",
                      color: "var(--text)",
                      textAlign: "center",
                      lineHeight: 1.3
                    }}>
