styles/globals.css.content:
@import url('https://fonts.googleapis.com/css2?family=Inter:wght@300;400;500;600;700;800;900&family=Space+Grotesk:wght@400;500;600;700&display=swap');

* {
  box-sizing: border-box;
  margin: 0;
  padding: 0;
}

:root {
  --primary: #7c3aed;
  --primary-dark: #6d28d9;
  --primary-light: #a78bfa;
  --secondary: #ec4899;
  --accent: #14b8a6;
  --accent-2: #f59e0b;

  --background: #0a0a0f;
  --surface: rgba(23, 23, 35, 0.8);
  --surface-light: rgba(40, 40, 55, 0.9);
  --surface-hover: rgba(55, 55, 75, 0.95);

  --text: #ffffff;
  --text-secondary: #a1a1aa;
  --text-muted: #71717a;

  --success: #22c55e;
  --error: #ef4444;

  --gradient-primary: linear-gradient(135deg, #667eea 0%, #764ba2 50%, #f093fb 100%);
  --gradient-accent: linear-gradient(135deg, #14b8a6 0%, #3b82f6 50%, #8b5cf6 100%);
  --gradient-warm: linear-gradient(135deg, #f59e0b 0%, #ef4444 50%, #ec4899 100%);

  --shadow-sm: 0 2px 8px rgba(0, 0, 0, 0.1);
  --shadow-md: 0 8px 24px rgba(0, 0, 0, 0.15);
  --shadow-lg: 0 20px 60px rgba(0, 0, 0, 0.3);
  --shadow-xl: 0 30px 90px rgba(0, 0, 0, 0.4);

  --shadow-glow-purple: 0 0 60px rgba(124, 58, 237, 0.4);
  --shadow-glow-blue: 0 0 60px rgba(59, 130, 246, 0.4);
  --shadow-glow-pink: 0 0 60px rgba(236, 72, 153, 0.4);

  --blur-sm: 8px;
  --blur-md: 16px;
  --blur-lg: 24px;

  --spacing-unit: 8px;
}

html,
body {
  font-family: 'Inter', -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Roboto', sans-serif;
  background: var(--background);
  color: var(--text);
  overflow-x: hidden;
  scroll-behavior: smooth;
  line-height: 1.7;
}

body {
  background: #0a0a0f;
  background-image: 
    radial-gradient(at 0% 0%, rgba(124, 58, 237, 0.15) 0px, transparent 50%),
    radial-gradient(at 100% 0%, rgba(59, 130, 246, 0.15) 0px, transparent 50%),
    radial-gradient(at 100% 100%, rgba(236, 72, 153, 0.15) 0px, transparent 50%),
    radial-gradient(at 0% 100%, rgba(20, 184, 166, 0.15) 0px, transparent 50%);
  min-height: 100vh;
  position: relative;
}

body::before {
  content: '';
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: 
    repeating-linear-gradient(
      0deg,
      rgba(255, 255, 255, 0.015) 0px,
      transparent 1px,
      transparent 2px,
      rgba(255, 255, 255, 0.015) 3px
    );
  pointer-events: none;
  z-index: 1;
  opacity: 0.5;
}

a {
  color: inherit;
  text-decoration: none;
}

button {
  font-family: inherit;
  cursor: pointer;
  border: none;
  outline: none;
}

input,
textarea,
select {
  font-family: inherit;
}

.container {
  max-width: 1400px;
  margin: 0 auto;
  padding: 0 2rem;
  position: relative;
  z-index: 2;
}

.display-font {
  font-family: 'Space Grotesk', 'Inter', sans-serif;
}

.gradient-text {
  background: linear-gradient(135deg, #a78bfa 0%, #ec4899 50%, #14b8a6 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  background-size: 200% auto;
}

.gradient-text-animated {
  animation: gradient-shift 4s ease infinite;
}

@keyframes gradient-shift {
  0%, 100% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
}

.btn {
  padding: 1.125rem 2.5rem;
  border-radius: 16px;
  font-weight: 600;
  font-size: 1rem;
  letter-spacing: -0.01em;
  transition: all 0.4s cubic-bezier(0.4, 0, 0.2, 1);
  display: inline-flex;
  align-items: center;
  justify-content: center;
  gap: 0.625rem;
  position: relative;
  overflow: hidden;
  white-space: nowrap;
}

.btn::before {
  content: '';
  position: absolute;
  top: 0;
  left: -100%;
  width: 100%;
  height: 100%;
  background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
  transition: left 0.6s;
}

.btn:hover::before {
  left: 100%;
}

.btn-primary {
  background: linear-gradient(135deg, #7c3aed 0%, #ec4899 100%);
  color: white;
  box-shadow: 0 12px 35px rgba(124, 58, 237, 0.35), 0 0 40px rgba(236, 72, 153, 0.2);
  border: 1px solid rgba(255, 255, 255, 0.1);
}

.btn-primary:hover {
  transform: translateY(-3px) scale(1.02);
  box-shadow: 0 18px 45px rgba(124, 58, 237, 0.45), 0 0 60px rgba(236, 72, 153, 0.3);
}

.btn-secondary {
  background: rgba(30, 30, 45, 0.8);
  backdrop-filter: blur(12px);
  color: var(--text);
  border: 2px solid rgba(167, 139, 250, 0.3);
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.2);
}

.btn-secondary:hover {
  background: rgba(45, 45, 65, 0.9);
  border-color: rgba(167, 139, 250, 0.5);
  transform: translateY(-3px);
  box-shadow: 0 12px 32px rgba(124, 58, 237, 0.25);
}

.btn:active {
  transform: translateY(-1px) scale(0.98);
}

.btn-ghost {
  background: transparent;
  border: 2px solid rgba(255, 255, 255, 0.15);
  color: var(--text);
}

.btn-ghost:hover {
  background: rgba(255, 255, 255, 0.05);
  border-color: rgba(167, 139, 250, 0.4);
}

.card {
  background: linear-gradient(135deg, rgba(23, 23, 35, 0.8) 0%, rgba(30, 30, 45, 0.7) 100%);
  backdrop-filter: blur(20px);
  border-radius: 24px;
  border: 1px solid rgba(255, 255, 255, 0.08);
  padding: 2.5rem;
  transition: all 0.5s cubic-bezier(0.4, 0, 0.2, 1);
  position: relative;
  overflow: hidden;
  box-shadow: 0 8px 32px rgba(0, 0, 0, 0.3);
}

.card::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  height: 2px;
  background: linear-gradient(90deg, transparent, rgba(167, 139, 250, 0.5), transparent);
  transform: scaleX(0);
  transition: transform 0.5s cubic-bezier(0.4, 0, 0.2, 1);
}

.card:hover::before {
  transform: scaleX(1);
}

.card:hover {
  transform: translateY(-8px);
  border-color: rgba(167, 139, 250, 0.25);
  box-shadow: 
    0 20px 60px rgba(0, 0, 0, 0.4),
    0 0 80px rgba(124, 58, 237, 0.2),
    inset 0 1px 0 rgba(255, 255, 255, 0.1);
}

.card-premium {
  background: linear-gradient(135deg, rgba(30, 30, 45, 0.9) 0%, rgba(40, 40, 60, 0.8) 100%);
  box-shadow: 
    0 20px 60px rgba(0, 0, 0, 0.4),
    0 0 100px rgba(124, 58, 237, 0.15),
    inset 0 1px 0 rgba(255, 255, 255, 0.1);
}

.glass {
  background: rgba(255, 255, 255, 0.03);
  backdrop-filter: blur(16px);
  border: 1px solid rgba(255, 255, 255, 0.1);
}

@keyframes fadeInUp {
  from {
    opacity: 0;
    transform: translateY(40px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

@keyframes scaleIn {
  from {
    opacity: 0;
    transform: scale(0.9);
  }
  to {
    opacity: 1;
    transform: scale(1);
  }
}

@keyframes pulse {
  0%, 100% { opacity: 1; }
  50% { opacity: 0.5; }
}

@keyframes shimmer {
  0% { background-position: -1000px 0; }
  100% { background-position: 1000px 0; }
}

@keyframes float {
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-20px); }
}

@keyframes glow {
  0%, 100% { filter: drop-shadow(0 0 20px rgba(167, 139, 250, 0.5)); }
  50% { filter: drop-shadow(0 0 40px rgba(236, 72, 153, 0.7)); }
}

.animate-fade-in {
  animation: fadeInUp 0.8s ease-out forwards;
}

input[type="text"],
input[type="number"],
textarea,
select {
  background: rgba(30, 30, 45, 0.6);
  border: 2px solid rgba(167, 139, 250, 0.2);
  border-radius: 14px;
  padding: 1.125rem 1.25rem;
  color: var(--text);
  font-size: 1rem;
  transition: all 0.3s ease;
  backdrop-filter: blur(10px);
}

input:focus,
textarea:focus,
select:focus {
  outline: none;
  border-color: rgba(167, 139, 250, 0.5);
  box-shadow: 
    0 0 0 4px rgba(124, 58, 237, 0.1),
    0 8px 24px rgba(124, 58, 237, 0.2);
  background: rgba(35, 35, 50, 0.8);
