@media (max-width: 1024px) {
  .container {
    padding: 0 1.5rem;
  }

  body::before {
    opacity: 0.3;
  }
}

@media (max-width: 768px) {
  .container {
    padding: 0 1.25rem;
  }

  .btn {
    padding: 1rem 2rem;
    font-size: 0.95rem;
  }

  .card {
    padding: 2rem 1.5rem;
