styles/Home.module.css.content:
.container {
  padding: 0 2rem;
}

.main {
  min-height: 100vh;
  padding: 4rem 0;
  flex: 1;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
}

.footer {
  display: flex;
  flex: 1;
  padding: 2rem 0;
  border-top: 1px solid #eaeaea;
  justify-content: center;
  align-items: center;
}

.footer a {
  display: flex;
  justify-content: center;
  align-items: center;
  flex-grow: 1;
}

.title a {
  color: #0070f3;
  text-decoration: none;
}

.title a:hover,
.title a:focus,
.title a:active {
  text-decoration: underline;
}

.title {
  margin: 0;
  line-height: 1.15;
  font-size: 4rem;
}

.title,
.description {
  text-align: center;
}

.description {
  margin: 4rem 0;
  line-height: 1.5;
  font-size: 1.5rem;
}

.code {
  background: #fafafa;
  border-radius: 5px;
  padding: 0.75rem;
  font-size: 1.1rem;
  font-family: Menlo, Monaco, Lucida Console, Liberation Mono, DejaVu Sans Mono,
    Bitstream Vera Sans Mono, Courier New, monospace;
}

.grid {
  display: flex;
  align-items: center;
  justify-content: center;
  flex-wrap: wrap;
  max-width: 800px;
}

.card {
  margin: 1rem;
  padding: 1.5rem;
  text-align: left;
  color: inherit;
  text-decoration: none;
  border: 1px solid #eaeaea;
  border-radius: 10px;
  transition: color 0.15s ease, border-color 0.15s ease;
  max-width: 300px;
}

.card:hover,
.card:focus,
.card:active {
  color: #0070f3;
  border-color: #0070f3;
}

.card h2 {
  margin: 0 0 1rem 0;
  font-size: 1.5rem;
}

.card p {
  margin: 0;
  font-size: 1.25rem;
  line-height: 1.5;
}

.logo {
  height: 1em;
  margin-left: 0.2rem;
}

@media (max-width: 600px) {
  .grid {
    width: 100%;
    flex-direction: column;
  }
}

pages/index.tsx.content:
import type { NextPage } from "next";
import Head from "next/head";
import { motion, AnimatePresence } from "framer-motion";
import { useState } from "react";
import { useRouter } from "next/router";
import Layout from "../components/Layout";
import { services } from "../data/services";
import { jobPositions } from "../data/jobs";

const Home: NextPage = () => {
  const router = useRouter();
  const [hoveredIndex, setHoveredIndex] = useState<number | null>(null);
  const [expandedJob, setExpandedJob] = useState<number | null>(null);
  const [hoveredService, setHoveredService] = useState<number | null>(null);
  const [expandedServiceBenefits, setExpandedServiceBenefits] = useState<number | null>(null);
  const [jobViewMode, setJobViewMode] = useState<Record<number, 'full-time' | 'intern'>>({});

  const handleApply = (jobTitle: string, isInternship: boolean = false) => {
    const applicationType = isInternship ? 'internship' : 'full-time';
    router.push(`/questionnaire?job=${encodeURIComponent(jobTitle)}&type=${applicationType}`);
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.08
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: {
      opacity: 1,
      y: 0
    }
  };

  return (
    <Layout>
      <div style={{ minHeight: "100vh", position: "relative" }}>
      <Head>
        <title>Cehpoint Jobs in West Bengal & India | Digital Marketing, Sales, Business Development Careers 2024</title>
        <meta name="description" content="Apply for top jobs in West Bengal, Kolkata, India at Cehpoint. Hiring Digital Marketing Lead, Growth Strategist, Business Development Executive, Sales Development Representative. Join India's leading technology solutions company." />
        <meta name="keywords" content="jobs in west bengal, jobs in kolkata, jobs in india, digital marketing jobs, sales jobs india, business development jobs, technology sales jobs, marketing jobs west bengal, cybersecurity sales jobs, IT sales jobs india, startup jobs india, work from home jobs india" />
        <meta name="viewport" content="width=device-width, initial-scale=1" />
        <link rel="icon" href="/favicon.ico" />

        <meta name="robots" content="index, follow" />
        <meta name="author" content="Cehpoint" />
        <meta name="geo.region" content="IN-WB" />
        <meta name="geo.placename" content="West Bengal" />
        <meta name="geo.position" content="22.5726;88.3639" />
        <meta name="ICBM" content="22.5726, 88.3639" />

        <meta property="og:title" content="Cehpoint Jobs - Digital Marketing, Sales & Business Development in West Bengal & India" />
        <meta property="og:description" content="Join Cehpoint's elite team. Apply for Digital Marketing Lead, Sales Development, Business Development positions. Technology sales & marketing careers in India." />
        <meta property="og:type" content="website" />
        <meta property="og:locale" content="en_IN" />
        <meta property="og:site_name" content="Cehpoint Careers" />

        <meta name="twitter:card" content="summary_large_image" />
        <meta name="twitter:title" content="Cehpoint Jobs in West Bengal & India | Apply Now" />
        <meta name="twitter:description" content="Premium technology sales & marketing careers. Digital Marketing, Business Development, Sales positions available." />

        <link rel="canonical" href="https://marketing-jobs.cehpoint.co.in" />

        <script type="application/ld+json">
          {JSON.stringify({
            "@context": "https://schema.org",
            "@type": "JobPosting",
            "title": "Multiple Positions Available",
            "description": "Cehpoint is hiring for Digital Marketing Lead, Growth Strategist, Business Development Executive, Sales Development Representative, and more positions in West Bengal and across India.",
            "datePosted": "2024-05-29",
            "hiringOrganization": {
              "@type": "Organization",
              "name": "Cehpoint",
              "sameAs": "https://marketing-jobs.cehpoint.co.in",
              "logo": "https://marketing-jobs.cehpoint.co.in/favicon.ico"
            },
            "jobLocation": {
              "@type": "Place",
              "address": {
                "@type": "PostalAddress",
                "addressRegion": "West Bengal",
                "addressCountry": "IN"
              }
            },
            "employmentType": "FULL_TIME",
            "baseSalary": {
              "@type": "MonetaryAmount",
              "currency": "INR",
              "value": {
                "@type": "QuantitativeValue",
                "minValue": 35000,
                "maxValue": 90000,
                "unitText": "MONTH"
              }
            }
          })}
        </script>
      </Head>

      <main>
        {/* Hero Section */}
        <section style={{ 
          padding: "8rem 0 6rem",
          position: "relative",
          overflow: "hidden"
        }}>
          <motion.div
            style={{
              position: "absolute",
              top: "20%",
              left: "10%",
              width: "400px",
              height: "400px",
              background: "radial-gradient(circle, rgba(124, 58, 237, 0.15) 0%, transparent 70%)",
              filter: "blur(80px)",
              pointerEvents: "none"
            }}
            animate={{
              scale: [1, 1.2, 1],
              opacity: [0.3, 0.5, 0.3],
            }}
            transition={{
              duration: 8,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          />

          <motion.div
            style={{
              position: "absolute",
              top: "40%",
              right: "10%",
              width: "500px",
              height: "500px",
              background: "radial-gradient(circle, rgba(236, 72, 153, 0.12) 0%, transparent 70%)",
              filter: "blur(90px)",
              pointerEvents: "none"
            }}
            animate={{
              scale: [1.2, 1, 1.2],
              opacity: [0.4, 0.2, 0.4],
            }}
            transition={{
              duration: 10,
              repeat: Infinity,
              ease: "easeInOut"
            }}
          />

          <div className="container">
            <motion.div
              initial={{ opacity: 0, y: 40 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 1, ease: [0.22, 1, 0.36, 1] }}
              style={{ textAlign: "center", maxWidth: "1100px", margin: "0 auto", position: "relative", zIndex: 2 }}
            >
              <motion.div
                initial={{ scale: 0.95, opacity: 0 }}
                animate={{ scale: 1, opacity: 1 }}
                transition={{ duration: 1.2, ease: [0.22, 1, 0.36, 1] }}
                style={{
                  display: "inline-block",
                  padding: "0.625rem 1.5rem",
                  background: "rgba(167, 139, 250, 0.1)",
                  border: "1px solid rgba(167, 139, 250, 0.2)",
                  borderRadius: "50px",
                  marginBottom: "2.5rem",
                  backdropFilter: "blur(10px)"
                }}
              >
                <span style={{ 
                  fontSize: "0.875rem", 
                  fontWeight: 600,
                  background: "linear-gradient(135deg, #a78bfa 0%, #ec4899 100%)",
                  WebkitBackgroundClip: "text",
                  WebkitTextFillColor: "transparent",
                  backgroundClip: "text"
                }}>
                  ðŸš€ Marketing & Business Development Opportunities - Join Our Growing Team
                </span>
              </motion.div>

              <motion.h1
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.2, duration: 0.8 }}
                className="display-font gradient-text gradient-text-animated"
                style={{
                  fontSize: "clamp(2.75rem, 8vw, 6rem)",
                  fontWeight: 700,
                  marginBottom: "1.5rem",
                  letterSpacing: "-0.04em",
                  lineHeight: 1.05
                }}
              >
                CEHPOINT
              </motion.h1>

              <motion.p
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay: 0.4, duration: 0.8 }}
                style={{
                  fontSize: "clamp(1.15rem, 2.2vw, 1.75rem)",
                  color: "var(--text-secondary)",
                  marginBottom: "1rem",
                  lineHeight: 1.65,
                  fontWeight: 400
                }}
              >
                Launch Your Marketing Career Where Innovation Meets Opportunity
              </motion.p>

              <motion.p
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay: 0.5, duration: 0.8 }}
                style={{
                  fontSize: "clamp(1rem, 2vw, 1.35rem)",
                  color: "var(--text-muted)",
                  marginBottom: "2rem",
                  lineHeight: 1.7
                }}
              >
                Build your skills, drive real results, and grow your career with India&apos;s ambitious technology company. We offer both full-time positions and internships tailored to your experience level.
              </motion.p>

              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ delay: 0.6, duration: 0.8 }}
                style={{
                  display: "flex",
                  gap: "1.5rem",
                  justifyContent: "center",
                  flexWrap: "wrap",
                  marginBottom: "3.5rem",
                  alignItems: "center"
                }}
              >
                <div
                  style={{
                    display: "flex",
                    alignItems: "center",
                    gap: "8px",
                    padding: "10px 18px",
                    background: "rgba(16, 185, 129, 0.1)",
                    border: "1px solid rgba(16, 185, 129, 0.3)",
                    borderRadius: "10px"
                  }}
                >
                  <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <path d="M9 12L11 14L15 10M21 12C21 16.9706 16.9706 21 12 21C7.02944 21 3 16.9706 3 12C3 7.02944 7.02944 3 12 3C16.9706 3 21 7.02944 21 12Z" stroke="#10B981" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                  </svg>
                  <span style={{ color: "#10B981", fontSize: "14px", fontWeight: "600" }}>
                    Verified Employer
                  </span>
                </div>
                <div
                  style={{
                    display: "flex",
                    alignItems: "center",
                    gap: "8px",
                    padding: "10px 18px",
                    background: "rgba(124, 58, 237, 0.1)",
                    border: "1px solid rgba(124, 58, 237, 0.3)",
                    borderRadius: "10px"
                  }}
                >
                  <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <path d="M13 10V3L4 14h7v7l9-11h-7z" stroke="#7C3AED" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                  </svg>
                  <span style={{ color: "#7C3AED", fontSize: "14px", fontWeight: "600" }}>
                    Performance-Based Growth
                  </span>
                </div>
                <div
                  style={{
                    display: "flex",
                    alignItems: "center",
                    gap: "8px",
                    padding: "10px 18px",
                    background: "rgba(236, 72, 153, 0.1)",
                    border: "1px solid rgba(236, 72, 153, 0.3)",
                    borderRadius: "10px"
                  }}
                >
                  <svg width="20" height="20" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <path d="M12 2L15.09 8.26L22 9.27L17 14.14L18.18 21.02L12 17.77L5.82 21.02L7 14.14L2 9.27L8.91 8.26L12 2Z" stroke="#EC4899" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                  </svg>
                  <span style={{ color: "#EC4899", fontSize: "14px", fontWeight: "600" }}>
                    Elite Team
                  </span>
                </div>
              </motion.div>

              <motion.div
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: 0.7, duration: 0.6 }}
                style={{ 
                  display: "flex", 
                  gap: "1.25rem", 
                  justifyContent: "center",
                  flexWrap: "wrap"
                }}
              >
                <motion.button
                  className="btn btn-primary"
                  onClick={() => {
                    document.getElementById('positions')?.scrollIntoView({ behavior: 'smooth' });
                  }}
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  style={{ fontSize: "1.1rem" }}
                >
                  Explore Opportunities â†’
                </motion.button>
                <motion.button
                  className="btn btn-secondary"
                  onClick={() => router.push('/sales-guide')}
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                  style={{ fontSize: "1.1rem", display: "flex", alignItems: "center", gap: "0.5rem" }}
                >
                  ðŸŽ“ Sales Guide
                </motion.button>
              </motion.div>
            </motion.div>
          </div>
        </section>

        {/* Services Section */}
        <section 
          style={{ 
            padding: "6rem 0",
            position: "relative",
            background: "linear-gradient(180deg, transparent 0%, rgba(124, 58, 237, 0.03) 50%, transparent 100%)"
          }}
        >
          <div className="container">
            <motion.div
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.8 }}
              style={{ textAlign: "center", marginBottom: "4rem" }}
            >
              <h2 
                className="display-font"
                style={{
                  fontSize: "clamp(2rem, 5vw, 3.5rem)",
                  fontWeight: 700,
                  marginBottom: "1.25rem",
                  letterSpacing: "-0.03em",
                  color: "var(--text)"
                }}
              >
                Our Services
              </h2>
              <p style={{
                fontSize: "clamp(1.05rem, 2vw, 1.3rem)",
                color: "var(--text-secondary)",
                maxWidth: "800px",
                margin: "0 auto 1rem"
              }}>
                Comprehensive technology solutions powering businesses across industries
              </p>
              <p style={{
                fontSize: "clamp(0.95rem, 1.8vw, 1.1rem)",
                color: "var(--text-muted)",
                maxWidth: "700px",
                margin: "0 auto"
              }}>
                Every role at Cehpoint directly contributes to delivering these cutting-edge services
              </p>
            </motion.div>

            <motion.div
              variants={containerVariants}
              initial="hidden"
              whileInView="visible"
              viewport={{ once: true }}
              style={{
                display: "grid",
                gridTemplateColumns: "repeat(auto-fit, minmax(min(100%, 280px), 1fr))",
                gap: "1.75rem",
                maxWidth: "1400px",
                margin: "0 auto"
              }}
            >
              {services.map((service, index) => (
                <motion.div
                  key={service.title}
                  variants={itemVariants}
                  onHoverStart={() => setHoveredService(index)}
                  onHoverEnd={() => setHoveredService(null)}
                  style={{
                    background: "linear-gradient(135deg, rgba(23, 23, 35, 0.8) 0%, rgba(30, 30, 45, 0.7) 100%)",
                    backdropFilter: "blur(20px)",
                    borderRadius: "20px",
                    border: "1px solid rgba(255, 255, 255, 0.08)",
                    padding: "2rem",
                    transition: "all 0.4s cubic-bezier(0.4, 0, 0.2, 1)",
                    position: "relative",
                    overflow: "hidden",
                    cursor: "pointer"
                  }}
                  whileHover={{ y: -8, borderColor: "rgba(167, 139, 250, 0.25)" }}
                >
                  <motion.div
                    style={{
                      position: "absolute",
                      top: 0,
                      left: 0,
                      right: 0,
                      bottom: 0,
                      background: service.gradient,
                      opacity: 0,
                      transition: "opacity 0.4s"
                    }}
                    animate={{
                      opacity: hoveredService === index ? 0.05 : 0
                    }}
                  />

                  <div style={{ position: "relative", zIndex: 1 }}>
                    <div style={{
                      fontSize: "3rem",
                      marginBottom: "1rem",
                      display: "flex",
                      alignItems: "center",
                      justifyContent: "center",
                      height: "80px"
                    }}>
                      {service.icon}
                    </div>

                    <h3 style={{
                      fontSize: "1.35rem",
                      fontWeight: 700,
                      marginBottom: "0.75rem",
                      color: "var(--text)",
                      textAlign: "center",
                      lineHeight: 1.3
                    }}>
                      {service.title}
                    </h3>

                    <p style={{
                      fontSize: "0.95rem",
                      color: "var(--text-secondary)",
                      marginBottom: "1rem",
                      lineHeight: 1.6,
                      textAlign: "center",
                      minHeight: "60px"
                    }}>
                      {service.description}
                    </p>

                    <div style={{
                      borderTop: "1px solid rgba(255, 255, 255, 0.08)",
                      paddingTop: "1rem",
                      marginTop: "1rem",
                      marginBottom: "1rem"
                    }}>
                      <p style={{
                        fontSize: "0.85rem",
                        color: "var(--primary-light)",
                        fontWeight: 600,
                        textAlign: "center",
                        lineHeight: 1.5,
                        marginBottom: "1rem"
                      }}>
                        {service.selling}
                      </p>

                      <div style={{
                        background: "rgba(124, 58, 237, 0.08)",
                        borderRadius: "10px",
                        padding: "1rem",
                        border: "1px solid rgba(124, 58, 237, 0.15)",
                        marginBottom: "0.75rem"
                      }}>
                        <div style={{
                          fontSize: "0.7rem",
                          fontWeight: 700,
                          color: "var(--primary-light)",
                          marginBottom: "0.5rem",
                          textTransform: "uppercase",
                          letterSpacing: "0.05em",
                          display: "flex",
                          alignItems: "center",
                          gap: "0.35rem"
                        }}>
                          <svg width="14" height="14" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                            <path d="M17 21v-2a4 4 0 0 0-4-4H5a4 4 0 0 0-4 4v2M9 11a4 4 0 1 0 0-8 4 4 0 0 0 0 8zM23 21v-2a4 4 0 0 0-3-3.87M16 3.13a4 4 0 0 1 0 7.75" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                          </svg>
                          Who Can Sell
                        </div>
                        <p style={{
                          fontSize: "0.85rem",
                          color: "var(--text-secondary)",
                          lineHeight: 1.5,
                          margin: 0
                        }}>
                          {service.whoCanSell}
                        </p>
                      </div>

                      <div style={{
                        background: "rgba(16, 185, 129, 0.08)",
                        borderRadius: "10px",
                        padding: "1rem",
                        border: "1px solid rgba(16, 185, 129, 0.15)",
                        marginBottom: "0.75rem"
                      }}>
                        <div style={{
                          fontSize: "0.7rem",
                          fontWeight: 700,
                          color: "#10B981",
                          marginBottom: "0.5rem",
                          textTransform: "uppercase",
                          letterSpacing: "0.05em",
                          display: "flex",
                          alignItems: "center",
                          gap: "0.35rem"
                        }}>
                          <svg width="14" height="14" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                            <path d="M9 11l3 3L22 4M21 12v7a2 2 0 01-2 2H5a2 2 0 01-2-2V5a2 2 0 012-2h11" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                          </svg>
                          How to Sell
                        </div>
                        <p style={{
                          fontSize: "0.85rem",
                          color: "var(--text-secondary)",
                          lineHeight: 1.5,
                          margin: 0
                        }}>
                          {service.howToSell}
                        </p>
                      </div>

                      <motion.button
                        onClick={(e) => {
                          e.stopPropagation();
                          setExpandedServiceBenefits(expandedServiceBenefits === index ? null : index);
                        }}
                        whileHover={{ scale: 1.02 }}
                        whileTap={{ scale: 0.98 }}
                        style={{
                          width: "100%",
                          background: "linear-gradient(135deg, rgba(124, 58, 237, 0.15) 0%, rgba(236, 72, 153, 0.15) 100%)",
                          border: "1px solid rgba(124, 58, 237, 0.3)",
                          borderRadius: "10px",
                          padding: "0.875rem 1rem",
                          color: "var(--primary-light)",
                          fontWeight: 600,
                          fontSize: "0.9rem",
                          cursor: "pointer",
                          transition: "all 0.3s ease",
                          display: "flex",
                          alignItems: "center",
                          justifyContent: "center",
                          gap: "0.5rem"
                        }}
                      >
                        <svg width="16" height="16" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
                          <path d="M21 10H3M21 6H3M21 14H3M21 18H3" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                        </svg>
                        {expandedServiceBenefits === index ? "Hide" : "View"} Benefits Package
                        <svg 
                          width="14" 
                          height="14" 
                          viewBox="0 0 24 24" 
                          fill="none" 
                          xmlns="http://www.w3.org/2000/svg"
                          style={{
                            transform: expandedServiceBenefits === index ? "rotate(180deg)" : "rotate(0deg)",
                            transition: "transform 0.3s ease"
                          }}
                        >
                          <path d="M6 9l6 6 6-6" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
                        </svg>
                      </motion.button>

                      <AnimatePresence>
                        {expandedServiceBenefits === index && (
                          <motion.div
                            initial={{ height: 0, opacity: 0 }}
                            animate={{ height: "auto", opacity: 1 }}
                            exit={{ height: 0, opacity: 0 }}
                            transition={{ duration: 0.3 }}
                            style={{ overflow: "hidden", marginTop: "0.75rem" }}
                          >
                            <div style={{
                              background: "rgba(124, 58, 237, 0.08)",
                              borderRadius: "10px",
                              padding: "1rem",
                              border: "1px solid rgba(124, 58, 237, 0.15)"
                            }}>
                              <p style={{
                                fontSize: "0.75rem",
                                color: "var(--text-muted)",
                                marginBottom: "0.75rem",
                                lineHeight: 1.5
                              }}>
                                <strong style={{ color: "var(--text)" }}>Compensation Structure:</strong> Fixed salary based on role + performance-based weekly incentives
                              </p>
                              <div style={{
                                background: "rgba(16, 185, 129, 0.1)",
                                borderRadius: "8px",
                                padding: "0.75rem",
                                marginBottom: "0.5rem"
