                  fontWeight: 600,
                  marginBottom: "2.5rem",
                  color: "var(--text)",
                  lineHeight: 1.4
                }}>
                  {question.id === 'salary_expectation' && isInternship 
                    ? question.question.replace('salary', 'stipend')
                    : question.question}
                </h2>

                {question.type === "yes-no" && (
                  <div style={{ display: "flex", gap: "1rem", flexWrap: "wrap" }}>
                    {["Yes", "No"].map((option) => (
                      <motion.button
                        key={option}
                        className="btn btn-secondary"
                        onClick={() => handleAnswer(option)}
                        whileHover={{ scale: 1.02, y: -2 }}
                        whileTap={{ scale: 0.98 }}
                        style={{
                          flex: 1,
                          minWidth: "150px",
                          padding: "1.25rem 2rem",
                          fontSize: "1.1rem",
                          background: answers[question.id] === option 
                            ? "linear-gradient(135deg, #7c3aed 0%, #ec4899 100%)"
                            : "rgba(255, 255, 255, 0.03)",
                          border: answers[question.id] === option
                            ? "1px solid rgba(167, 139, 250, 0.4)"
                            : "1px solid rgba(255, 255, 255, 0.08)"
                        }}
                      >
                        {option}
                      </motion.button>
                    ))}
                  </div>
                )}

                {(question.type === "select" || question.type === "experience") && (
                  <div style={{ display: "flex", flexDirection: "column", gap: "0.875rem" }}>
                    {question.options?.map((option) => (
                      <motion.button
                        key={option}
                        className="btn btn-secondary"
                        onClick={() => handleAnswer(option)}
                        whileHover={{ scale: 1.01, x: 5 }}
                        whileTap={{ scale: 0.99 }}
                        style={{
                          padding: "1.15rem 1.75rem",
                          fontSize: "1.05rem",
                          textAlign: "left",
                          background: answers[question.id] === option 
                            ? "linear-gradient(135deg, #7c3aed 0%, #ec4899 100%)"
                            : "rgba(255, 255, 255, 0.03)",
                          border: answers[question.id] === option
                            ? "1px solid rgba(167, 139, 250, 0.4)"
                            : "1px solid rgba(255, 255, 255, 0.08)"
                        }}
                      >
                        {option}
                      </motion.button>
                    ))}
                  </div>
                )}

                {question.type === "text" && (
                  <div>
                    <textarea
                      value={textInput}
                      onChange={(e) => {
                        setTextInput(e.target.value);
                        setValidationErrors({}); // Clear validation errors on typing
                      }}
                      placeholder="Type your answer here..."
                      style={{
                        width: "100%",
                        minHeight: "150px",
                        padding: "1.25rem",
                        fontSize: "1.05rem",
                        background: "rgba(255, 255, 255, 0.03)",
                        border: validationErrors[question.id] ? "1px solid #ef4444" : "1px solid rgba(255, 255, 255, 0.08)",
                        borderRadius: "12px",
                        color: "var(--text)",
                        marginBottom: "0.5rem",
                        resize: "vertical",
                        fontFamily: "inherit",
                        lineHeight: 1.6
                      }}
                    />
                    {validationErrors[question.id] && (
                      <p style={{ color: "#ef4444", fontSize: "0.875rem", marginBottom: "1.5rem" }}>
                        {validationErrors[question.id]}
                      </p>
                    )}
                    <motion.button
                      className="btn btn-primary"
                      onClick={() => textInput.trim() && handleAnswer(textInput)}
                      disabled={!textInput.trim()}
                      whileHover={{ scale: textInput.trim() ? 1.02 : 1 }}
                      whileTap={{ scale: textInput.trim() ? 0.98 : 1 }}
                      style={{
                        width: "100%",
                        padding: "1.15rem",
                        fontSize: "1.1rem",
                        opacity: textInput.trim() ? 1 : 0.5,
                        cursor: textInput.trim() ? "pointer" : "not-allowed"
                      }}
                    >
                      Continue â†’
                    </motion.button>
                  </div>
                )}

                {question.type === "salary" && (
                  <div>
                    <div style={{ position: "relative" }}>
                      <span style={{
                        position: "absolute",
                        left: "1.25rem",
                        top: "50%",
                        transform: "translateY(-50%)",
                        fontSize: "1.15rem",
                        color: "var(--text-muted)"
                      }}>
                        â‚¹
                      </span>
                      <input
                        type="number"
                        value={textInput}
                        onChange={(e) => {
                          setTextInput(e.target.value);
                          setValidationErrors({}); // Clear validation errors on typing
                        }}
                        placeholder="50000"
                        style={{
                          width: "100%",
                          padding: "1.15rem 1.25rem 1.15rem 2.5rem",
                          fontSize: "1.15rem",
                          background: "rgba(255, 255, 255, 0.03)",
                          border: validationErrors[question.id] ? "1px solid #ef4444" : "1px solid rgba(255, 255, 255, 0.08)",
                          borderRadius: "12px",
                          color: "var(--text)",
                          marginBottom: "0.5rem"
                        }}
                      />
                    </div>
                    <motion.button
                      className="btn btn-primary"
                      onClick={() => textInput.trim() && handleAnswer(textInput)}
                      disabled={!textInput.trim()}
                      whileHover={{ scale: textInput.trim() ? 1.02 : 1 }}
                      whileTap={{ scale: textInput.trim() ? 0.98 : 1 }}
                      style={{
                        width: "100%",
                        padding: "1.15rem",
                        fontSize: "1.1rem",
                        opacity: textInput.trim() ? 1 : 0.5,
                        cursor: textInput.trim() ? "pointer" : "not-allowed"
                      }}
                    >
                      Continue â†’
                    </motion.button>
                  </div>
                )}
              </div>
            </motion.div>
          </AnimatePresence>

          {currentQuestion > 0 && (
            <motion.button
              initial={{ opacity: 0 }}
              animate={{ opacity: 1 }}
              onClick={handleBack}
              className="btn btn-secondary"
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
              style={{
                marginTop: "1.5rem",
                width: "100%",
                padding: "1rem",
                fontSize: "1rem"
              }}
            >
              â† Back
            </motion.button>
          )}
        </div>
      </main>
    </div>
  );
};

export default Questionnaire;

